mapper.update:
update community
set
community_title = #{communityTitle}, 
community_content = #{communityContent}, 
community_state = #{communityState,jdbcType=VARCHAR} 
where community_no = #{communityNo}

update community
set community_views = community_views + 1 
where community_no  = #{communityNo}

update community
set community_likes = community_likes + 1 
where community_no = #{communityNo}
