mapper.update:
update game
set game_user_score = (
    CASE 
        WHEN (select count(*) from member_review where game_no = #{gameNo}) = 0 THEN 0
        ELSE (
            select ROUND(AVG(review_score), 1)
            from member_review
            where game_no = #{gameNo}
        )
    END
)
where game_no = #{gameNo}

update game
set game_review_count = (
    select count(*)
    from member_review
    where game_no = #{gameNo}
)
where game_no = #{gameNo}
